import React, { useEffect, useState } from 'react';
import axios from "axios";
import {Line} from 'react-chartjs-2'

const API_URL =
  "https://covid-api.mmediagroup.fr/v1/history?country=Australia&status=confirmed";

function getData() {
  var labels, nswCases, vicCases
  fetch(API_URL).then((res) => res.json()).then(function(data){
    var labels = Object.keys(data["All"].dates).reverse();
    //NSW DATA
    var nswCases = Object.values(data["New South Wales"].dates).reverse();
    nswCases = nswCases.map((v, i, a) => v - (a[i - 1] || 0));
    //VIC DATA
    var vicCases = Object.values(data["Victoria"].dates).reverse();
    vicCases = vicCases.map((v, i, a) => v - (a[i - 1] || 0));
    return { labels, nswCases, vicCases };
  })

};


const CovidChart = () => {
    var api_data = getData();
    console.log(api_data);
    var nswCases, vicCases, labels;
    const[covidData, setData] = useState(null);
    useEffect( () => {
      setData(api_data);
      console.log(covidData);
    }, [])


    return ( 
       <div>
           <button>1W</button>
           <button>1M</button>
           <button>3M</button>
        <Line
          data={{
            labels: labels,
            datasets: [
              {
                label: "NSW",
                data: nswCases,
                fill: false,
                borderColor: "#36a2eb",
                tension: 0.1,
              },
              {
                label: "Vic",
                data: vicCases,
                fill: false,
                borderColor: "#ff6384",
                tension: 0.1,
              }
            ],
          }}
        />
      </div>
    );
}

export default CovidChart;
