import React, { useEffect, useState } from "react";
import axios from "axios";
import {Line} from 'react-chartjs-2'

const API_URL = "https://covid-api.mmediagroup.fr/v1/history?country=Australia&status=confirmed";


const CovidChart = () => {
    const [data, setData] = useState(null);
    useEffect(() => {
        axios.get(API_URL).then(res => setData(res.data));
    }, []);


    if (data != null) {
     var labels = Object.keys(data["All"].dates).slice(1,30).reverse();
        //NSW DATA 
      var nswCases = Object.values(data["New South Wales"].dates).slice(0,30).reverse();
      //VIC DATA
      var vicCases = Object.values(data["Victoria"].dates).slice(0,30).reverse();
    }
    return (
      <div>
        <Line
          data={{
            labels: labels,
            datasets: [
              {
                label: "NSW",
                data: nswCases.map((n,i,a) => i ? n-a[i-1]: 0-n),
                fill: false,
                borderColor: "#36a2eb",
                tension: 0.1,
              },
              {
                label: "Vic",
                data: vicCases.map((n,i,a) => i ? n-a[i-1]: 0-n),
                fill: false,
                borderColor: "#ff6384",
                tension: 0.1,
              }
            ],
          }}
        />
      </div>
    );
}

export default CovidChart;
